import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import Main from "./components/Main";
import Signup from "./components/Signup";
import Login from "./components/Login";
import Setting from "./components/Settings";
import Questions from "./components/Questions";
import FinalScreen from "./components/FinalScreen/index";
import { Container, Typography } from "@mui/material";
import "./App.css";
import { Box } from "@mui/system";

function App() {
  const user = localStorage.getItem("token");
  return (
    <div className="App">
      <Typography><h1 className="header">QuizZ Application</h1></Typography>
      <Routes>
       {user &&<Route path="/" exact element={<Main />} />}
       <Route path="/signup" exact element={<Signup />}/>
       <Route path="/login" exact element={<Login />}/>
       <Route path="/" exact element={<Navigate replace to="/login" />}/>
       <Route path="/settings" exact element={<Setting />}/>
       <Route path="/questions" exact element={<Questions />}/>
       <Route path="/finalScreen" exact element={<FinalScreen />}/>
     </Routes>
      {/* <Router>
        <Container maxWidth="sm">
